Get-Content -path "ChoiceVDatabaseGenerator\Model\Database\ChoiceVDb.cs" | % { $_ -Replace 'UseMySql\(".*",', 'UseMySql($"server={Config.DatabaseIp};port={Config.DatabasePort};database={Config.DatabaseDatabase};username={Config.DatabaseUser};password={Config.DatabasePassword}",' } |  Out-File "Temp.txt"; Get-Content -path "Temp.txt" | Out-File "ChoiceVDatabaseGenerator\Model\Database\ChoiceVDb.cs"; Remove-Item -path "Temp.txt"
Get-Content -path "ChoiceVDatabaseGenerator\Model\FsDatabase\ChoiceVFsDb.cs" | % { $_ -Replace 'UseMySql\(".*",', 'UseMySql($"server={Config.FVSDatabaseIp};port={Config.FVSDatabasePort};database={Config.FVSDatabaseDatabase};username={Config.FVSDatabaseUser};password={Config.FVSDatabasePassword}",' } |  Out-File "Temp.txt"; Get-Content -path "Temp.txt" | Out-File "ChoiceVDatabaseGenerator\Model\FsDatabase\ChoiceVFsDb.cs"; Remove-Item -path "Temp.txt"