Get-Content -path "Model\Database\WhitelistDb.cs" | % { $_ -Replace 'UseMySql\(".*",', 'UseMySql($"server={Config.DatabaseIp};port={Config.DatabasePort};database={Config.DatabaseDatabase};username={Config.DatabaseUser};password={Config.DatabasePassword}",' } |  Out-File "Temp.txt"; Get-Content -path "Temp.txt" | Out-File "Model\Database\WhitelistDb.cs"; Remove-Item -path "Temp.txt"